import i18n from 'i18next';
import { initReactI18next } from 'react-i18next';
import LanguageDetector from 'i18next-browser-languagedetector';

i18n
  .use(LanguageDetector)
  .use(initReactI18next)
  .init({
    resources: {
      en: {
        translation: {
          welcome: "Welcome",
          login: "Login",
          signup: "Signup"
        }
      },
      fr: {
        translation: {
          welcome: "Bienvenue",
          login: "Connexion",
          signup: "S'inscrire"
        }
      },
      de: {
        translation: {
          welcome: "Willkommen",
          login: "Anmelden",
          signup: "Registrieren"
        }
      }
    },
    fallbackLng: "en",
    interpolation: {
      escapeValue: false
    }
  });

export default i18n;
