import React, { useEffect, useState } from 'react';

const ClientProjectList = () => {
  const [projects, setProjects] = useState([]);
  const clientEmail = localStorage.getItem("userEmail");

  useEffect(() => {
    if (!clientEmail) return;

    fetch(`http://localhost:8081/api/projects/client/${clientEmail}`)
      .then(res => res.json())
      .then(data => setProjects(data))
      .catch(err => console.error("Error fetching client projects:", err));
  }, [clientEmail]);

  return (
    <div style={{ padding: '2rem' }}>
      <h2>My Posted Projects</h2>
      {projects.length === 0 ? (
        <p>You havenâ€™t posted any projects yet.</p>
      ) : (
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {projects.map((project, index) => (
            <li key={index} style={{ border: '1px solid #ccc', padding: '1rem', borderRadius: '8px', marginBottom: '1rem' }}>
              <h3>{project.title} </h3>
              <p><strong>Category:</strong> {project.category}</p>
              <p><strong>Description:</strong> {project.description}</p>
              <p><strong>Deadline Date:</strong> {new Date(project.deadline).toLocaleDateString()}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default ClientProjectList;
