import React, { useEffect, useState } from 'react';

const ClientProjectList = () => {
  const [projects, setProjects] = useState([]);
  const clientEmail = localStorage.getItem("userEmail");

  useEffect(() => {
    if (!clientEmail) return;

    fetch(`http://localhost:8081/api/projects/client/${clientEmail}`)
      .then((res) => {
        if (!res.ok) throw new Error('Failed to fetch projects');
        return res.json();
      })
      .then((data) => setProjects(data))
      .catch((err) => console.error("Error fetching client projects:", err));
  }, [clientEmail]);

  return (
    <div style={styles.container}>
      <div style={styles.topBar}>
        <h2 style={styles.heading}>My Posted Projects</h2>
      </div>
      {projects.length === 0 ? (
        <p style={styles.noClients}>You havenâ€™t posted any projects yet.</p>
      ) : (
        <ul style={styles.list}>
          {projects.map((project, index) => (
            <li key={index} style={styles.card}>
              <h3>{project.title}</h3>
              <p><strong>Category:</strong> {project.category}</p>
              <p><strong>Description:</strong> {project.description}</p>
              <p><strong>Deadline:</strong> {new Date(project.deadline).toLocaleDateString()}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

/* ðŸ”’ Same styles as your FreelancerChatList */
const styles = {
  container: {
    padding: '2rem',
    maxWidth: '900px',
    margin: '0 auto',
    fontFamily: "'Segoe UI', sans-serif"
  },
  topBar: {
    display: 'flex',
    justifyContent: 'center',
    marginBottom: '1rem'
  },
  heading: {
    textAlign: 'center',
    color: '#007acc',
    marginBottom: '1.5rem'
  },
  noClients: {
    textAlign: 'center',
    color: '#666'
  },
  list: {
    listStyle: 'none',
    padding: 0,
    display: 'grid',
    gap: '1.5rem'
  },
  card: {
    padding: '1.5rem',
    borderRadius: '10px',
    backgroundColor: '#f9fcff',
    border: '1px solid #cce6ff',
    boxShadow: '0 4px 12px rgba(0, 123, 255, 0.1)'
  }
};

export default ClientProjectList;
