import React, { useState, useEffect, useRef } from 'react';
import '@fortawesome/fontawesome-free/css/all.min.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';
import { useTranslation } from 'react-i18next';
import { useNavigate } from 'react-router-dom';
import './Hero.css';

/* ðŸ”– Default categories in case backend fails */
const defaultCategories = [
  'Web Development',
  'Graphic Design',
  'Digital Marketing',
  'Content Writing',
  'Data Science',
  'Video Editing',
  'UI/UX Design',
  'Mobile App Development',
];

const Hero = () => {
  const { t } = useTranslation();
  const [searchInput, setSearchInput] = useState('');
  const navigate = useNavigate();
  const inputRef = useRef(null);

  const handleSearch = () => {
    if (searchInput.trim() !== '') {
      navigate(`/explore/${searchInput}`);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSearch();
    }
  };

  return (
    <div className="hero-container d-flex align-items-center justify-content-center text-center">
      <div className="overlay"></div>
      <div className="hero-content">
        <h1
          style={{
            fontSize: '3rem',
            fontWeight: 'bold',
            color: '#fff',
            marginTop: '20px',
          }}
        >
          Welcome to SkillFusion
        </h1>
        <p
          style={{
            fontSize: '1.3rem',
            color: '#fff',
            marginTop: '10px',
            fontWeight: '500',
          }}
        >
          Fuel Your Passion, Upgrade Your Skills.
        </p>
        <div className="input-group mt-4 w-75 mx-auto">
          <input
            type="text"
            className="form-control"
            placeholder={t('Search for services...')}
            value={searchInput}
            ref={inputRef}
            onChange={(e) => setSearchInput(e.target.value)}
            onKeyPress={handleKeyPress}
          />
          <button className="btn btn-primary" onClick={handleSearch}>
            <i className="fas fa-search"></i>
          </button>
        </div>
      </div>
    </div>
  );
};

export default Hero;
