import React from 'react';
import './ExploreServices';
import {
  Code,
  Palette,
  Bot,
  TrendingUp,
  PenTool,
  Users,
  Building2,
  Scale,
  GraduationCap,
  Settings
} from 'lucide-react';
import './ExploreServices.css'; // Make sure this CSS file exists

const ExploreServices = () => {
  const services = [
    { icon: <Code size={28} />, title: "Development & IT", subtitle: "Custom software solutions and technical expertise" },
    { icon: <Palette size={28} />, title: "Design & Creative", subtitle: "Visual design and creative content services" },
    { icon: <Bot size={28} />, title: "AI Services", subtitle: "Machine learning and AI-powered solutions" },
    { icon: <TrendingUp size={28} />, title: "Sales & Marketing", subtitle: "Growth strategies and marketing campaigns" },
    { icon: <PenTool size={28} />, title: "Writing & Translation", subtitle: "Content creation and language services" },
    { icon: <Users size={28} />, title: "Admin & Support", subtitle: "Administrative and customer support services" },
    { icon: <Building2 size={28} />, title: "Finance & Accounting", subtitle: "Financial planning and accounting services" },
    { icon: <Scale size={28} />, title: "Legal", subtitle: "Legal consultation and compliance services" },
    { icon: <GraduationCap size={28} />, title: "HR & Training", subtitle: "Human resources and professional development" },
    { icon: <Settings size={28} />, title: "Engineering & Architecture", subtitle: "Technical engineering and design solutions" }
  ];

  return (
    <section className="explore-section">
      <div className="explore-header">
        <h2>Explore millions of pros</h2>
        <p>Find the perfect professional for your project from our diverse range of expert services</p>
      </div>

      <div className="services-grid">
        {services.map((service, index) => (
          <div key={index} className="service-card">
            <div className="icon-wrapper">{service.icon}</div>
            <h3>{service.title}</h3>
            <p>{service.subtitle}</p>
          </div>
        ))}
      </div>

      <div className="explore-footer">
        <button className="browse-button">Browse All Categories</button>
      </div>
    </section>
  );
};

export default ExploreServices;
