import React, { useEffect, useState } from 'react';
import './ProfilePage.css'; // 🔁 Add this line

const ProfilePage = () => {
  const email = localStorage.getItem("userEmail");
  const [user, setUser] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [form, setForm] = useState({ name: '', role: '' });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    if (!email) {
      setError('No email found in localStorage');
      setLoading(false);
      return;
    }

    fetch(`http://localhost:8081/api/auth/${email}`)
      .then(res => {
        if (!res.ok) throw new Error("Failed to fetch user");
        return res.json();
      })
      .then(data => {
        setUser(data);
        setForm({ name: data.name, role: data.role });
        setLoading(false);
      })
      .catch(err => {
        console.error(err);
        setError('Failed to load profile.');
        setLoading(false);
      });
  }, [email]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm(prev => ({ ...prev, [name]: value }));
  };

  const handleUpdate = async () => {
    try {
      const res = await fetch(`http://localhost:8081/api/auth/${email}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ ...user, ...form }),
      });

      if (res.ok) {
        alert('Profile updated successfully');
        setEditMode(false);
        setUser(prev => ({ ...prev, ...form }));
      } else {
        alert('Update failed');
      }
    } catch (err) {
      alert('Server error');
      console.error(err);
    }
  };

  if (loading) return <p className="error">Loading profile...</p>;
  if (error) return <p className="error">{error}</p>;

  return (
    <div className="profile-container">
      <h2>User Profile</h2>
      <p><strong>Email:</strong> {user.email}</p>

      {editMode ? (
        <>
          <label>Name</label>
          <input
            name="name"
            value={form.name}
            onChange={handleChange}
          />

          <label>Role</label>
          <select
            name="role"
            value={form.role}
            onChange={handleChange}
          >
            <option value="CLIENT">CLIENT</option>
            <option value="FREELANCER">FREELANCER</option>
          </select>

          <button onClick={handleUpdate}>Save</button>
        </>
      ) : (
        <>
          <p><strong>Name:</strong> {user.name}</p>
          <p><strong>Role:</strong> {user.role}</p>
          <button className="edit-btn" onClick={() => setEditMode(true)}>Edit</button>
        </>
      )}
    </div>
  );
};

export default ProfilePage;
