import React, { useState, useEffect, useRef } from 'react';
import { useTranslation } from 'react-i18next';
import './Navbar.css';

const Navbar = () => {
  const { t, i18n } = useTranslation();
  const [showLang, setShowLang] = useState(false);
  const langRef = useRef(null);

  const changeLanguage = (lng) => {
    i18n.changeLanguage(lng);
    setShowLang(false);
  };

  // Close dropdown if clicked outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (langRef.current && !langRef.current.contains(event.target)) {
        setShowLang(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  return (
    <nav className="navbar">
      <div className="navbar-logo">
        <h2>fiverr<span className="dot">.</span></h2>
      </div>

      <div className="navbar-right">
        <ul className="nav-links">
          <li><a href="#">Fiverr Pro ‚ñæ</a></li>
          <li><a href="#">Explore ‚ñæ</a></li>

          {/* Language dropdown - click controlled */}
          <li ref={langRef} className="lang-dropdown" onClick={() => setShowLang(!showLang)}>
            üåê {i18n.language.toUpperCase()}
            {showLang && (
              <ul className="lang-menu">
                <li onClick={() => changeLanguage('en')}>English</li>
                <li onClick={() => changeLanguage('fr')}>Fran√ßais</li>
                <li onClick={() => changeLanguage('de')}>Deutsch</li>
                <li onClick={() => changeLanguage('es')}>Espa√±ol</li>
                <li onClick={() => changeLanguage('pt')}>Portugu√™s</li>
                <li onClick={() => changeLanguage('it')}>Italiano</li>
              </ul>
            )}
          </li>

          <li><a href="#" className="login-btn">{t('login')}</a></li>
          <li><a href="#" className="signup-btn">{t('signup')}</a></li>
        </ul>
      </div>
    </nav>
  );
};

export default Navbar;
