import React, { useEffect, useState, useRef } from "react";
import SockJS from "sockjs-client";
import { Client } from "@stomp/stompjs";
import axios from "axios";
import "./ChatBox.css";

const ChatBox = ({ senderId, receiverId }) => {
  const [message, setMessage] = useState("");
  const [chatHistory, setChatHistory] = useState([]);
  const stompClientRef = useRef(null);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    connectWebSocket();
    fetchChatHistory();
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [chatHistory]);

  const connectWebSocket = () => {
    const socket = new SockJS("http://localhost:8008/ws");
    const stompClient = new Client({
      webSocketFactory: () => socket,
      onConnect: () => {
        stompClient.subscribe("/topic/messages", onMessageReceived);
      },
      onStompError: (frame) => {
        console.error("Broker error: " + frame.headers["message"]);
      },
    });
    stompClient.activate();
    stompClientRef.current = stompClient;
  };

  const onMessageReceived = (payload) => {
    const msg = JSON.parse(payload.body);
    const isCurrentChat =
      (msg.senderId === senderId && msg.receiverId === receiverId) ||
      (msg.senderId === receiverId && msg.receiverId === senderId);

    if (isCurrentChat) {
      setChatHistory((prev) => [...prev, msg]);
    }
  };

  const fetchChatHistory = async () => {
    try {
      const response = await axios.get("http://localhost:8008/api/chat/history", {
        params: { senderId, receiverId },
      });
      setChatHistory(response.data);
    } catch (err) {
      console.error("Failed to load chat history", err);
    }
  };

  const sendMessage = () => {
    if (!message.trim()) return;

    const chatMessage = {
      senderId,
      receiverId,
      content: message,
      timestamp: new Date().toISOString(),
    };

    stompClientRef.current?.publish({
      destination: "/app/chat.send",
      body: JSON.stringify(chatMessage),
    });

    setMessage("");
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  return (
    <div className="chat-container">
      <div className="chat-header">Chat with User {receiverId}</div>

      <div className="chat-messages">
        {chatHistory.map((msg, idx) => (
          <div
            key={idx}
            className={`chat-bubble ${msg.senderId === senderId ? "sent" : "received"}`}
          >
            {msg.content}
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      <div className="chat-input-area">
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Type your message..."
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
};

export default ChatBox;
