import React from 'react';
import { useTranslation } from 'react-i18next';
import './Navbar.css';

const Navbar = () => {
  const { t, i18n } = useTranslation();

  const changeLanguage = (lng) => {
    i18n.changeLanguage(lng);
  };

  const scrollToPremium = () => {
    const section = document.getElementById('premium-section');
    if (section) {
      section.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <nav className="navbar">
      {/* Left Logo */}
      <div className="navbar-left">
        <h2 className="logo">
          SkillsFusion<span className="dot">.</span>
        </h2>
      </div>

      {/* Right Group */}
      <div className="navbar-right-group">
        <ul className="navbar-center">
          <li>
            <button className="nav-btn" onClick={scrollToPremium}>
              {t('fusionPro')}
            </button>
          </li>
          <li><a href="#">{t('explore')}</a></li>

          {/* Language Dropdown */}
          <li className="lang-dropdown">
            üåê {i18n.language.toUpperCase()}
            <ul className="lang-menu">
              <li onClick={() => changeLanguage('en')}>English</li>
              <li onClick={() => changeLanguage('fr')}>Fran√ßais</li>
              <li onClick={() => changeLanguage('de')}>Deutsch</li>
              <li onClick={() => changeLanguage('es')}>Espa√±ol</li>
              <li onClick={() => changeLanguage('pt')}>Portugu√™s</li>
            </ul>
          </li>
        </ul>

        {/* Auth Buttons */}
        <div className="navbar-right">
          <a href="#" className="login-btn">{t('login')}</a>
          <a href="#" className="join-btn">{t('signup')}</a>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
