import React, { useState, useEffect, useRef } from 'react';
import '@fortawesome/fontawesome-free/css/all.min.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';
import { useTranslation } from 'react-i18next';
import { useNavigate } from 'react-router-dom';
import './Hero.css';

const Hero = () => {
  const { t } = useTranslation();
  const navigate = useNavigate();
  const [searchTerm, setSearchTerm] = useState('');
  const [allCategories, setAllCategories] = useState([]);
  const [filteredCategories, setFilteredCategories] = useState([]);
  const [showSuggestions, setShowSuggestions] = useState(false);
  const searchRef = useRef(null);
  const suggestionsRef = useRef(null);

  const userEmail = localStorage.getItem('userEmail');
  const userRole = localStorage.getItem('userRole');
  const isFreelancer = userEmail && userRole === 'FREELANCER';

  // Fetch categories from backend on load
  useEffect(() => {
    fetch('http://localhost:8080/api/projects/categories')
      .then((res) => res.json())
      .then((data) => {
        if (Array.isArray(data)) {
          setAllCategories(data);
        }
      })
      .catch((err) => console.error('Failed to load categories:', err));
  }, []);

  // Close suggestions on outside click
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (
        searchRef.current &&
        !searchRef.current.contains(event.target) &&
        suggestionsRef.current &&
        !suggestionsRef.current.contains(event.target)
      ) {
        setShowSuggestions(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const navigateToCategory = (category) => {
    const encoded = encodeURIComponent(category);
    navigate(`/category?category=${encoded}`, {
      state: { category },
    });
    setShowSuggestions(false);
  };

  const handleSearch = () => {
    const trimmed = searchTerm.trim();
    if (trimmed) navigateToCategory(trimmed);
  };

  const handleChange = (e) => {
    const value = e.target.value;
    setSearchTerm(value);

    if (!value.trim()) {
      setFilteredCategories([]);
      setShowSuggestions(false);
      return;
    }

    const filtered = allCategories.filter((cat) =>
      cat.toLowerCase().includes(value.toLowerCase())
    );
    setFilteredCategories(filtered);
    setShowSuggestions(filtered.length > 0);
  };

  const handleSuggestionClick = (category) => {
    setSearchTerm(category);
    navigateToCategory(category);
  };

  return (
    <section className="hero-section">
      {/* 🎞 Video Carousel */}
      <div id="videoCarousel" className="carousel slide carousel-fade" data-bs-ride="carousel">
        <div className="carousel-inner">
          {['hero.mp4', 'hero2.mp4', 'hero3.mp4'].map((vid, i) => (
            <div className={`carousel-item ${i === 0 ? 'active' : ''}`} key={vid}>
              <video className="d-block w-100" autoPlay muted loop>
                <source src={`/Assets/${vid}`} type="video/mp4" />
              </video>
            </div>
          ))}
        </div>
        <button className="carousel-control-prev" type="button" data-bs-target="#videoCarousel" data-bs-slide="prev">
          <span className="carousel-control-prev-icon"></span>
        </button>
        <button className="carousel-control-next" type="button" data-bs-target="#videoCarousel" data-bs-slide="next">
          <span className="carousel-control-next-icon"></span>
        </button>
      </div>

      {/* 📝 Headline */}
      <div className="hero-content">
        {!isFreelancer ? (
          <>
            <h1 style={{ fontSize: '3rem', fontWeight: 'bold', color: '#ffffff', marginTop: '20px' }}>
              Welcome to SkillNest
            </h1>
            <p style={{ fontSize: '1.2rem', color: '#ffffff', marginTop: '10px' }}>
              Your one-stop destination to connect with expert freelancers & clients.
            </p>
          </>
        ) : (
          <>
            <h1 className="display-7 fw-bold">{t('heroTitle')}</h1>
            <p>{t('heroSubtitle')}</p>
          </>
        )}

        {/* ✅ Dynamic Search */}
        {isFreelancer && (
          <div className="hero-search" ref={searchRef}>
            <div className="hero-search-bar">
              <input
                type="text"
                placeholder={t('searchPlaceholder', 'Search for any service...')}
                value={searchTerm}
                onChange={handleChange}
                onFocus={() => {
                  if (filteredCategories.length > 0) setShowSuggestions(true);
                }}
              />
              <button onClick={handleSearch}>
                <i className="fas fa-search"></i>
              </button>
            </div>

            {/* 🔽 Autocomplete Suggestions */}
            {showSuggestions && (
              <ul
                className="suggestion-dropdown"
                ref={suggestionsRef}
                style={{
                  listStyle: 'none',
                  padding: '0',
                  margin: '5px 0 0',
                  backgroundColor: '#fff',
                  border: '1px solid #ddd',
                  borderRadius: '5px',
                  maxHeight: '200px',
                  overflowY: 'auto',
                  boxShadow: '0 2px 5px rgba(0, 0, 0, 0.1)',
                }}
              >
                {filteredCategories.map((cat, index) => (
                  <li
                    key={index}
                    style={{
                      padding: '10px',
                      cursor: 'pointer',
                      borderBottom: '1px solid #eee',
                    }}
                    onClick={() => handleSuggestionClick(cat)}
                    onMouseEnter={(e) => (e.target.style.background = '#f8f9fa')}
                    onMouseLeave={(e) => (e.target.style.background = '#fff')}
                  >
                    {cat}
                  </li>
                ))}
              </ul>
            )}

            {/* 👇 Suggested Buttons */}
            <div className="hero-suggestions">
              <button onClick={() => navigateToCategory('website development')}>
                {t('websiteDev', 'website development →')}
              </button>
              <button onClick={() => navigateToCategory('architecture')}>
                {t('architectureDesign', 'architecture & interior design →')}
              </button>
              <button onClick={() => navigateToCategory('video editing')}>
                {t('videoEdit', 'video editing →')}
              </button>
            </div>
          </div>
        )}
      </div>
    </section>
  );
};

export default Hero;
