import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';

const CategoryProjectList = () => {
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const category = queryParams.get('category');

  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    if (!category) return;

    setLoading(true);
    setError('');
    window.scrollTo(0, 0); // auto-scroll to top on category change

    fetch(`http://localhost:8080/api/projects/category/${encodeURIComponent(category)}`)
      .then((res) => {
        if (!res.ok) throw new Error('Failed to fetch projects');
        return res.json();
      })
      .then((data) => {
        setProjects(data);
        setLoading(false);
      })
      .catch((err) => {
        console.error('Error fetching:', err);
        setError('Something went wrong while fetching projects.');
        setLoading(false);
      });
  }, [category]);

  return (
    <div className="category-page" style={{ padding: '20px' }}>
      <h2 style={{ marginBottom: '20px' }}>
        Projects in Category: <span style={{ color: '#007bff' }}>{category || 'N/A'}</span>
      </h2>

      {loading ? (
        <p>Loading projects...</p>
      ) : error ? (
        <p style={{ color: 'red' }}>{error}</p>
      ) : projects.length === 0 ? (
        <p>No projects found in this category.</p>
      ) : (
        <ul className="project-list" style={{ listStyle: 'none', padding: 0 }}>
          {projects.map((p, index) => (
            <li
              key={index}
              className="project-card"
              style={{
                background: '#f9f9f9',
                padding: '15px',
                marginBottom: '15px',
                borderRadius: '8px',
                boxShadow: '0 2px 5px rgba(0,0,0,0.1)',
              }}
            >
              <h3>{p.title}</h3>
              <p><strong>Description:</strong> {p.description}</p>
              <p><strong>Budget:</strong> â‚¹{p.budget}</p>
              <p><strong>Deadline:</strong> {p.deadline}</p>
              <p><strong>Client Name:</strong> {p.clientName}</p>
              <p><strong>Client Email:</strong> {p.clientEmail}</p>
              <p><strong>Contact Number:</strong> {p.contactNumber}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default CategoryProjectList;
