// src/components/Hero.js
import React, { useState, useEffect, useRef } from 'react';
import '@fortawesome/fontawesome-free/css/all.min.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';
import { useTranslation } from 'react-i18next';
import { useNavigate } from 'react-router-dom';
import './Hero.css';

/* ðŸ”– Update this list any time you add new highâ€‘level categories */
const categories = [
  'Development & IT',
  'Design & Creative',
  'AI Services',
  'Sales & Marketing',
  'Engineering & Architecture',
  'Writing & Translation',
  'Customer Support',
  'Data Science',
  'Cybersecurity',
  'Mobile App Development',
];

const Hero = () => {
  const { t } = useTranslation();
  const navigate  = useNavigate();

  // â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ state
  const [searchTerm, setSearchTerm]             = useState('');
  const [filteredCategories, setFiltered]       = useState([]);
  const [showSuggestions,  setShowSuggestions]  = useState(false);

  // â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ refs (for clickâ€‘outside logic)
  const inputRef       = useRef(null);
  const dropdownRef    = useRef(null);

  // â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ auth filter
  const userEmail = localStorage.getItem('userEmail');
  const userRole  = localStorage.getItem('userRole');
  const isFreelancer = userEmail && userRole === 'FREELANCER';

  /* â•­â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â•®
     â”‚ Close dropdown when user clicks out â”‚
     â•°â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â•¯ */
  useEffect(() => {
    function handleOutside(e) {
      if (
        dropdownRef.current &&
        !dropdownRef.current.contains(e.target) &&
        inputRef.current &&
        !inputRef.current.contains(e.target)
      ) {
        setShowSuggestions(false);
      }
    }
    document.addEventListener('mousedown', handleOutside);
    return () => document.removeEventListener('mousedown', handleOutside);
  }, []);

  /* â•­â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â•®
     â”‚ Handle typing in the search field   â”‚
     â•°â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â•¯ */
  const handleChange = (e) => {
    const value = e.target.value;
    setSearchTerm(value);

    if (value.trim() === '') {
      setFiltered([]);
      setShowSuggestions(false);
      return;
    }
    const matches = categories.filter((cat) =>
      cat.toLowerCase().includes(value.toLowerCase())
    );
    setFiltered(matches);
    setShowSuggestions(matches.length > 0);
  };

  /* â•­â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â•®
     â”‚ Navigate helper (query + state)     â”‚
     â•°â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â•¯ */
  const goToCategory = (category) => {
    const url = `/category?category=${encodeURIComponent(category)}`;
    navigate(url, { state: { category } });  // pass in BOTH places
    setShowSuggestions(false);
  };

  /* â•­â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â•®
     â”‚ Search button / form submit         â”‚
     â•°â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â•¯ */
  const handleSearch = (e) => {
    if (e) e.preventDefault();
    const trimmed = searchTerm.trim();
    if (trimmed) goToCategory(trimmed);
  };

  return (
    <section className="hero-section">
      {/* â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘ VIDEO CAROUSEL â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘ */}
      <div id="videoCarousel" className="carousel slide carousel-fade" data-bs-ride="carousel">
        <div className="carousel-inner">
          {['hero.mp4', 'hero2.mp4', 'hero3.mp4'].map((vid, idx) => (
            <div key={vid} className={`carousel-item ${idx === 0 ? 'active' : ''}`}>
              <video className="d-block w-100" autoPlay muted loop>
                <source src={`/Assets/${vid}`} type="video/mp4" />
              </video>
            </div>
          ))}
        </div>
        <button className="carousel-control-prev" type="button" data-bs-target="#videoCarousel" data-bs-slide="prev">
          <span className="carousel-control-prev-icon"></span>
        </button>
        <button className="carousel-control-next" type="button" data-bs-target="#videoCarousel" data-bs-slide="next">
          <span className="carousel-control-next-icon"></span>
        </button>
      </div>

      {/* â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘  HERO COPY â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘ */}
      <div className="hero-content">
        <h1 className="display-7 fw-bold">{t('heroTitle')}</h1>
        <p>{t('heroSubtitle')}</p>

        {/* â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘ SEARCH BAR â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘ */}
        {isFreelancer && (
          <div className="hero-search" style={{ position: 'relative' }}>
            <form className="hero-search-bar" onSubmit={handleSearch}>
              <input
                ref={inputRef}
                type="text"
                placeholder={t('searchPlaceholder', 'Search for any service...')}
                value={searchTerm}
                onChange={handleChange}
                onFocus={() => {
                  if (filteredCategories.length > 0) setShowSuggestions(true);
                }}
              />
              <button type="submit">
                <i className="fas fa-search"></i>
              </button>
            </form>

            {/* â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘ AUTOCOMPLETE â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘â–‘ */}
            {showSuggestions && (
              <ul
                ref={dropdownRef}
                className="suggestion-dropdown"
                style={{
                  position: 'absolute',
                  top: '100%', left: 0, right: 0,
                  zIndex: 20,
                  background: '#fff',
                  border: '1px solid #ddd',
                  borderRadius: '5px',
                  marginTop: '4px',
                  maxHeight: '200px',
                  overflowY: 'auto',
                  listStyle: 'none',
                  padding: 0,
                }}
              >
                {filteredCategories.map((cat) => (
                  <li
                    key={cat}
                    onClick={() => goToCategory(cat)}
                    style={{
                      padding: '10px',
                      cursor: 'pointer',
                      borderBottom: '1px solid #eee',
                    }}
                    onMouseEnter={(e) => (e.currentTarget.style.background = '#f8f9fa')}
                    onMouseLeave={(e) => (e.currentTarget.style.background = '#fff')}
                  >
                    {cat}
