import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';
import { useTranslation } from 'react-i18next';
import './Hero.css';

const Hero = () => {
  const { t } = useTranslation();

  // üîê Get user role from localStorage
  const userRole = localStorage.getItem('userRole');
  const isFreelancer = userRole === 'FREELANCER';

  return (
    <section className="hero-section">
      {/* üîÅ Video Carousel */}
      <div id="videoCarousel" className="carousel slide carousel-fade" data-bs-ride="carousel">
        <div className="carousel-inner">
          <div className="carousel-item active">
            <video className="d-block w-100" autoPlay muted loop>
              <source src="/Assets/hero.mp4" type="video/mp4" />
            </video>
          </div>
          <div className="carousel-item">
            <video className="d-block w-100" autoPlay muted loop>
              <source src="/Assets/hero2.mp4" type="video/mp4" />
            </video>
          </div>
          <div className="carousel-item">
            <video className="d-block w-100" autoPlay muted loop>
              <source src="/Assets/hero3.mp4" type="video/mp4" />
            </video>
          </div>
        </div>

        <button className="carousel-control-prev" type="button" data-bs-target="#videoCarousel" data-bs-slide="prev">
          <span className="carousel-control-prev-icon"></span>
        </button>
        <button className="carousel-control-next" type="button" data-bs-target="#videoCarousel" data-bs-slide="next">
          <span className="carousel-control-next-icon"></span>
        </button>
      </div>

      {/* üìç Headline */}
      <div className="hero-content">
        <h1 className="display-7 fw-bold">Find the Perfect Client</h1>

        {/* üîç Search section visible only to freelancers */}
        {isFreelancer && (
          <div className="hero-search">
            <div className="hero-search-bar">
              <input type="text" placeholder="Search for any service..." />
              <button><i className="fas fa-search"></i></button>
            </div>
            <div className="hero-suggestions">
              <button>website development ‚Üí</button>
              <button>architecture & interior design ‚Üí</button>
              <button>video editing ‚Üí</button>
            </div>
          </div>
        )}
      </div>
    </section>
  );
};

export default Hero;
