import React, { useEffect, useState } from 'react';
import './ChatPage.css';
import { useSearchParams, useNavigate } from 'react-router-dom';

const ChatPage = () => {
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState('');
  const [searchParams] = useSearchParams();
  const navigate = useNavigate();

  const senderEmail = localStorage.getItem('userEmail');
  const receiverEmail = searchParams.get('to');

  const fetchMessages = async () => {
    try {
      const res = await fetch(
        `http://localhost:8081/api/chat/messages?user1=${senderEmail}&user2=${receiverEmail}`
      );
      const data = await res.json();
      setMessages(data);
    } catch (error) {
      console.error('Error fetching messages:', error);
    }
  };

  const sendMessage = async () => {
    if (!text.trim()) return;
    try {
      await fetch('http://localhost:8081/api/chat/send', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          senderEmail,
          receiverEmail,
          message: text,
        }),
      });
      setText('');
      fetchMessages();
    } catch (error) {
      console.error('Error sending message:', error);
    }
  };

  useEffect(() => {
    fetchMessages();
    const interval = setInterval(fetchMessages, 3000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="chat-container">
      <button className="back-button" onClick={() => navigate(-1)}>‚Üê Back</button>
      <h2 className="chat-title">Chat with {receiverEmail}</h2>
      <div className="chat-box">
        {messages.map((msg, index) => (
          <div
            key={index}
            className={`chat-message ${msg.senderEmail === senderEmail ? 'sent' : 'received'}`}
          >
            <p>{msg.message}</p>
            <span className="timestamp">{new Date(msg.timestamp).toLocaleTimeString()}</span>
          </div>
        ))}
      </div>
      <div className="chat-input">
        <input
          value={text}
          onChange={(e) => setText(e.target.value)}
          placeholder="Type a message..."
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
};

export default ChatPage;
