import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const FreelancerChatList = () => {
  const [clients, setClients] = useState([]);
  const freelancerEmail = localStorage.getItem('userEmail');
  const navigate = useNavigate();

  useEffect(() => {
    if (!freelancerEmail) return;

    fetch(`http://localhost:8081/api/applications/freelancer/${freelancerEmail}`)
      .then((res) => {
        if (!res.ok) throw new Error('Failed to fetch clients');
        return res.json();
      })
      .then((data) => setClients(data))
      .catch((err) => console.error('Error fetching clients:', err));
  }, [freelancerEmail]);

  const handleChat = (clientEmail) => {
    navigate(`/chat?to=${clientEmail}`);
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Your Clients</h2>
      {clients.length === 0 ? (
        <p>You haven't applied to any projects yet.</p>
      ) : (
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {clients.map((c, idx) => (
            <li
              key={idx}
              style={{
                marginBottom: '1rem',
                border: '1px solid #ccc',
                padding: '1rem',
                borderRadius: '8px',
              }}
            >
              <p><strong>Client Name:</strong> {c.project.clientName}</p>
              <p><strong>Email:</strong> {c.project.clientEmail}</p>
              <p><strong>Project:</strong> {c.project.title}</p>
              <button
                onClick={() => handleChat(c.project.clientEmail)}
                style={{
                  padding: '0.5rem 1rem',
                  backgroundColor: '#00c6ff',
                  color: 'white',
                  border: 'none',
                  borderRadius: '6px',
                  cursor: 'pointer',
                }}
              >
                Chat
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default FreelancerChatList;
