import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';            /* ← ADD */
import './CategoryProjectList.css';                                     /* ← ADD */

const CategoryProjectList = () => {
  const location   = useLocation();
  const navigate   = useNavigate();                                     /* ← ADD */
  const queryParams = new URLSearchParams(location.search);
  const category    = queryParams.get('category');

  const [projects, setProjects] = useState([]);
  const [loading,  setLoading]  = useState(false);
  const [error,    setError]    = useState('');

  useEffect(() => {
    if (!category) return;
    setLoading(true);
    setError('');
    window.scrollTo(0, 0);

    fetch(`http://localhost:8081/api/projects/category/${encodeURIComponent(category)}`)
      .then((res) => {
        if (!res.ok) throw new Error('Failed to fetch projects');
        return res.json();
      })
      .then((data) => { setProjects(data); setLoading(false); })
      .catch((err) => { console.error(err); setError('Something went wrong while fetching projects.'); setLoading(false); });
  }, [category]);

  /* ─── JSX ───────────────────────────────────────────── */
  return (
    <div className="category-page">
      {/* 🔙 Back Button */}
      <button className="back-btn" onClick={() => navigate('/')}>← Back to Home</button>

      <h2 className="page-title">
        Projects in Category: <span>{category || 'N/A'}</span>
      </h2>

      {loading ? (
        <p className="status-msg">Loading projects...</p>
      ) : error ? (
        <p className="status-msg error">{error}</p>
      ) : projects.length === 0 ? (
        <p className="status-msg">No projects found in this category.</p>
      ) : (
        <ul className="project-list">
          {projects.map((p, i) => (
            <li key={i} className="project-card fade-in-up">
              <h3>{p.title}</h3>
              <p><strong>Description:</strong> {p.description}</p>
              <p><strong>Budget:</strong> ₹{p.budget}</p>
              <p><strong>Deadline:</strong> {p.deadline}</p>
              <p><strong>Client Name:</strong> {p.clientName}</p>
              <p><strong>Client Email:</strong> {p.clientEmail}</p>
              <p><strong>Contact Number:</strong> {p.contactNumber}</p>

              {/* 📨 Apply Button */}
              <button className="apply-btn" onClick={() => alert(`Applied for ${p.title}`)}>
                Apply Now
              </button>
              use
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default CategoryProjectList;
