import React, { useState, useEffect, useRef } from 'react';
import '@fortawesome/fontawesome-free/css/all.min.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';
import { useTranslation } from 'react-i18next';
import { useNavigate } from 'react-router-dom';
import './Hero.css';

const categories = [
  'Development & IT',
  'Design & Creative',
  'AI Services',
  'Sales & Marketing',
  'Engineering & Architecture',
  'Writing & Translation',
  'Customer Support',
  'Data Science',
  'Cybersecurity',
  'Mobile App Development',
];

const Hero = () => {
  const { t } = useTranslation();
  const navigate = useNavigate();
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredCategories, setFilteredCategories] = useState([]);
  const [showSuggestions, setShowSuggestions] = useState(false);
  const searchRef = useRef(null);
  const suggestionsRef = useRef(null);

  const userEmail = localStorage.getItem('userEmail');
  const userRole = localStorage.getItem('userRole');
  const isFreelancer = userEmail && userRole === 'FREELANCER';

  // Handle outside click to close dropdown
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (
        searchRef.current &&
        !searchRef.current.contains(event.target) &&
        suggestionsRef.current &&
        !suggestionsRef.current.contains(event.target)
      ) {
        setShowSuggestions(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const handleSearch = () => {
    const trimmed = searchTerm.trim();
    if (trimmed) {
      navigate(`/categorydetails?category=${encodeURIComponent(trimmed)}`);
      setShowSuggestions(false);
    }
  };

  const handleChange = (e) => {
    const value = e.target.value;
    setSearchTerm(value);

    if (value.trim() === '') {
      setFilteredCategories([]);
      setShowSuggestions(false);
      return;
    }

    const filtered = categories.filter((cat) =>
      cat.toLowerCase().includes(value.toLowerCase())
    );
    setFilteredCategories(filtered);
    setShowSuggestions(filtered.length > 0);
  };

  const handleSuggestionClick = (category) => {
    setSearchTerm(category);
    setShowSuggestions(false);
    navigate(`/category?category=${encodeURIComponent(category)}`);
  };

  return (
    <section className="hero-section">
      {/* üéû Video Carousel */}
      <div id="videoCarousel" className="carousel slide carousel-fade" data-bs-ride="carousel">
        <div className="carousel-inner">
          <div className="carousel-item active">
            <video className="d-block w-100" autoPlay muted loop>
              <source src="/Assets/hero.mp4" type="video/mp4" />
            </video>
          </div>
          <div className="carousel-item">
            <video className="d-block w-100" autoPlay muted loop>
              <source src="/Assets/hero2.mp4" type="video/mp4" />
            </video>
          </div>
          <div className="carousel-item">
            <video className="d-block w-100" autoPlay muted loop>
              <source src="/Assets/hero3.mp4" type="video/mp4" />
            </video>
          </div>
        </div>

        <button className="carousel-control-prev" type="button" data-bs-target="#videoCarousel" data-bs-slide="prev">
          <span className="carousel-control-prev-icon"></span>
        </button>
        <button className="carousel-control-next" type="button" data-bs-target="#videoCarousel" data-bs-slide="next">
          <span className="carousel-control-next-icon"></span>
        </button>
      </div>

      {/* üìù Headline */}
      <div className="hero-content">
        <h1 className="display-7 fw-bold">{t('heroTitle')}</h1>
        <p>{t('heroSubtitle')}</p>

        {/* ‚úÖ Dynamic Search */}
        {isFreelancer && (
          <div className="hero-search" ref={searchRef}>
            <div className="hero-search-bar">
              <input
                type="text"
                placeholder={t('searchPlaceholder', 'Search for any service...')}
                value={searchTerm}
                onChange={handleChange}
                onFocus={() => {
                  if (filteredCategories.length > 0) setShowSuggestions(true);
                }}
              />
              <button onClick={handleSearch}>
                <i className="fas fa-search"></i>
              </button>
            </div>

            {/* üîΩ Autocomplete Suggestions */}
            {showSuggestions && (
              <ul
                className="suggestion-dropdown"
                ref={suggestionsRef}
                style={{
                  listStyle: 'none',
                  padding: '0',
                  margin: '5px 0 0',
                  backgroundColor: '#fff',
                  border: '1px solid #ddd',
                  borderRadius: '5px',
                  maxHeight: '200px',
                  overflowY: 'auto',
                  boxShadow: '0 2px 5px rgba(0, 0, 0, 0.1)',
                }}
              >
                {filteredCategories.map((cat, index) => (
                  <li
                    key={index}
                    style={{
                      padding: '10px',
                      cursor: 'pointer',
                      borderBottom: '1px solid #eee',
                    }}
                    onClick={() => handleSuggestionClick(cat)}
                    onMouseEnter={(e) => (e.target.style.background = '#f8f9fa')}
                    onMouseLeave={(e) => (e.target.style.background = '#fff')}
                  >
                    {cat}
                  </li>
                ))}
              </ul>
            )}

            {/* üëá Suggested Buttons (same UI) */}
            <div className="hero-suggestions">
              <button onClick={() => navigate('/category?category=website development')}>
                {t('websiteDev', 'website development ‚Üí')}
              </button>
              <button onClick={() => navigate('/category?category=architecture')}>
                {t('architectureDesign', 'architecture & interior design ‚Üí')}
              </button>
              <button onClick={() => navigate('/category?category=video editing')}>
                {t('videoEdit', 'video editing ‚Üí')}
              </button>
            </div>
          </div>
        )}
      </div>
    </section>
  );
};

export default Hero;
