// src/components/Navbar.js
import React, { useState, useEffect } from 'react';
import { useTranslation } from 'react-i18next';
import { useNavigate } from 'react-router-dom';
import { User, LogOut, ChevronDown, Plus, MessageCircle } from 'lucide-react';

/**
 *  Navbar
 *  ‚îÄ Keeps your original **inline‚Äëstyled** blue‚Äëgradient look
 *  ‚îÄ Adds /clientpro (‚ÄúCreate¬†Project‚Äù) and /chatlist (‚ÄúChat‚Äù) shortcuts
 *  ‚îÄ Preserves language switcher, profile, and logout logic
 */
const Navbar = () => {
  const { t, i18n } = useTranslation();
  const navigate = useNavigate();

  // ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ state
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userEmail, setUserEmail] = useState('');
  const [userRole, setUserRole] = useState('');
  const [showUserMenu, setShowUserMenu] = useState(false);
  const [showLangMenu, setShowLangMenu] = useState(false);

  // ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ effects
  useEffect(() => {
    const authStatus = localStorage.getItem('isAuthenticated');
    const email      = localStorage.getItem('userEmail');
    const role       = localStorage.getItem('userRole');

    if (authStatus === 'true') {
      setIsAuthenticated(true);
      setUserEmail(email || '');
      setUserRole(role || '');
    }
  }, []);

  // ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ handlers
  const changeLanguage = (lng) => {
    i18n.changeLanguage(lng);
    setShowLangMenu(false);
  };

  const handleLogout = () => {
    localStorage.clear();
    setIsAuthenticated(false);
    setUserEmail('');
    setUserRole('');
    setShowUserMenu(false);
    window.dispatchEvent(new Event('authStateChanged'));   // üîî let other components know
    navigate('/');
  };

  const handleProfileClick       = () => { navigate('/profile');   setShowUserMenu(false); };
  const handleCreateProjectClick = () => { navigate('/clientpro'); setShowUserMenu(false); };
  const handleChatClick          = () => { navigate('/chatlist');  setShowUserMenu(false); };

  // ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ render
  return (
    <nav style={navBarStyle}>
      {/* ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ logo ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ */}
      <div className="navbar-left">
        <h2
          style={logoStyle}
          onClick={() => navigate('/')}
          onMouseEnter={(e) => (e.currentTarget.style.color = '#0099cc')}
          onMouseLeave={(e) => (e.currentTarget.style.color = '#00c6ff')}
        >
          SkillsFusion<span style={{ color: '#ff6b6b' }}>.</span>
        </h2>
      </div>

      {/* ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ center links + language ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ */}
      <div style={{ display: 'flex', alignItems: 'center', gap: '2rem' }}>
        <ul style={ulStyle}>
          <li><button onClick={() => navigate('/premium')} style={navLinkStyle}>{t('fusionPro')}</button></li>
          <li><button onClick={() => navigate('/explore')} style={navLinkStyle}>{t('explore')}</button></li>

          {/* language switcher */}
          <li
            style={langDropdownStyle}
            onMouseEnter={() => setShowLangMenu(true)}
            onMouseLeave={() => setShowLangMenu(false)}
          >
            üåê {i18n.language.toUpperCase()}
            <ul style={langMenuStyle(showLangMenu)}>
              {['en','fr','de','es','pt'].map((lng) => (
                <li key={lng} onClick={() => changeLanguage(lng)} style={langMenuItemStyle}>
                  {{ en:'English', fr:'Fran√ßais', de:'Deutsch', es:'Espa√±ol', pt:'Portugu√™s' }[lng]}
                </li>
              ))}
            </ul>
          </li>
        </ul>

        {/* ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ right‚Äëhand buttons / user menu ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ */}
        <div style={{ display:'flex', alignItems:'center', gap:'1rem' }}>
          {isAuthenticated ? (
            <div style={{ position:'relative' }}>
              {/* user chip */}
              <div
                style={userChipStyle}
                onClick={() => setShowUserMenu(!showUserMenu)}
              >
                <User size={18}/><span>{userEmail.split('@')[0]}</span><ChevronDown size={16}/>
              </div>

              {/* dropdown */}
              {showUserMenu && (
                <div style={userMenuStyle}>
                  <div onClick={handleProfileClick}        style={dropdownItemStyle}><User         size={16}/> <span>Profile</span></div>

                  {userRole === 'CLIENT' && (
                    <>
                      <div onClick={handleCreateProjectClick} style={dropdownItemStyle}><Plus          size={16}/> <span>Add¬†Project</span></div>
                      <div onClick={handleChatClick}          style={dropdownItemStyle}><MessageCircle size={16}/> <span>Chat</span></div>
                    </>
                  )}

                  <div onClick={handleLogout} style={{ ...dropdownItemStyle, borderTop:'1px solid #e5e7eb', marginTop:'4px', color:'#ef4444' }}>
                    <LogOut size={16}/> <span>Logout</span>
                  </div>
                </div>
              )}
            </div>
          ) : (
            <>
              <button onClick={() => navigate('/login')}  style={loginBtnStyle}>{t('login')}</button>
              <button onClick={() => navigate('/signup')} style={joinBtnStyle}>{t('signup')}</button>
            </>
          )}
        </div>
      </div>
    </nav>
  );
};

