import React from 'react';
import { useTranslation } from 'react-i18next';
import { useNavigate } from 'react-router-dom';
import './Navbar.css';

const Navbar = () => {
  const { t, i18n } = useTranslation();
  const navigate = useNavigate();

  const changeLanguage = (lng) => {
    i18n.changeLanguage(lng);
  };

  return (
    <nav className="navbar">
      {/* Left Logo */}
      <div className="navbar-left">
        <h2 className="logo">
          SkillsFusion<span className="dot">.</span>
        </h2>
      </div>

      {/* Right Group */}
      <div className="navbar-right-group">
        <ul className="navbar-center">
          <li>
            <a onClick={() => navigate('/premium')} className="nav-link" style={{ cursor: 'pointer' }}>
              {t('fusionPro')}
            </a>
          </li>

          {/* ‚úÖ Explore as a button instead of anchor */}
          <li>
            <button
              onClick={() => navigate('/development')}
              style={{
                background: 'none',
                border: 'none',
                cursor: 'pointer',
                color: 'inherit',
                font: 'inherit',
                padding: 0,
              }}
              className="nav-link"
            >
              {t('explore')}
            </button>
          </li>

          {/* Language Dropdown */}
          <li className="lang-dropdown">
            üåê {i18n.language.toUpperCase()}
            <ul className="lang-menu">
              <li onClick={() => changeLanguage('en')}>English</li>
              <li onClick={() => changeLanguage('fr')}>Fran√ßais</li>
              <li onClick={() => changeLanguage('de')}>Deutsch</li>
              <li onClick={() => changeLanguage('es')}>Espa√±ol</li>
              <li onClick={() => changeLanguage('pt')}>Portugu√™s</li>
            </ul>
          </li>
        </ul>

        {/* Auth Buttons */}
        <div className="navbar-right">
          <a onClick={() => navigate('/login')} className="login-btn" style={{ cursor: 'pointer' }}>
            {t('login')}
          </a>
          <a onClick={() => navigate('/signup')} className="join-btn" style={{ cursor: 'pointer' }}>
            {t('signup')}
          </a>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
