import React, { useEffect, useState } from 'react';
import { useNavigate, useSearchParams } from 'react-router-dom';

const ChatPage = () => {
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState('');
  const [searchParams] = useSearchParams();
  const navigate = useNavigate();

  const senderEmail = localStorage.getItem('userEmail');
  const receiverEmail = searchParams.get('to'); // ?to=email

  const fetchMessages = async () => {
    try {
      const res = await fetch(
        `http://localhost:8081/api/chat/messages?user1=${senderEmail}&user2=${receiverEmail}`
      );
      const data = await res.json();
      setMessages(data);
    } catch (error) {
      console.error('Error fetching messages:', error);
    }
  };

  const sendMessage = async () => {
    if (!text.trim()) return;
    try {
      await fetch('http://localhost:8081/api/chat/send', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          senderEmail,
          receiverEmail,
          message: text,
        }),
      });
      setText('');
      fetchMessages();
    } catch (error) {
      console.error('Error sending message:', error);
    }
  };

  useEffect(() => {
    fetchMessages();
    const interval = setInterval(fetchMessages, 1000);
    return () => clearInterval(interval);
  }, [receiverEmail]);

  return (
    <div
      style={{
        height: '100vh',
        width: '100vw',
        backgroundColor: '#e9ecef',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
      }}
    >
      <div
        style={{
          height: '90vh',
          width: '80vw',
          maxWidth: '1000px',
          backgroundColor: '#ffffff',
          border: '1px solid #ccc',
          borderRadius: '12px',
          padding: '20px',
          display: 'flex',
          flexDirection: 'column',
          boxShadow: '0 4px 20px rgba(0,0,0,0.1)',
          position: 'relative',
        }}
      >
        {/* Back button inside chat box, top-left */}
        <button
          onClick={() => navigate('/')}
          style={{
            position: 'absolute',
            top: '20px',
            left: '20px',
            padding: '8px 16px',
            backgroundColor: '#007bff',
            color: 'white',
            border: 'none',
            borderRadius: '6px',
            cursor: 'pointer',
          }}
        >
          ‚Üê Back
        </button>

        <h2 style={{ textAlign: 'center', marginBottom: '60px' }}>
          Chat with {receiverEmail}
        </h2>

        {/* Chat messages */}
        <div
          style={{
            flexGrow: 1,
            overflowY: 'auto',
            padding: '10px',
            backgroundColor: '#f8f9fa',
            borderRadius: '8px',
            marginBottom: '20px',
          }}
        >
          {messages.map((msg, index) => (
            <div
              key={index}
              style={{
                display: 'flex',
                flexDirection: 'column',
                alignItems: msg.senderEmail === senderEmail ? 'flex-end' : 'flex-start',
                marginBottom: '10px',
              }}
            >
              <div
                style={{
                  padding: '10px',
                  borderRadius: '10px',
                  maxWidth: '70%',
                  backgroundColor:
                    msg.senderEmail === senderEmail ? '#d1e7dd' : '#f8d7da',
                  color: '#000',
                }}
              >
                <p style={{ margin: 0 }}>{msg.message}</p>
                <span style={{ fontSize: '0.8rem', color: '#555' }}>
                  {new Date(msg.timestamp).toLocaleTimeString()}
                </span>
              </div>
            </div>
          ))}
        </div>

        {/* Chat input */}
        <div
          style={{
            display: 'flex',
            alignItems: 'center',
            gap: '10px',
          }}
        >
          <input
            value={text}
            onChange={(e) => setText(e.target.value)}
            placeholder="Type a message..."
            style={{
              flexGrow: 1,
              padding: '12px',
              borderRadius: '6px',
              border: '1px solid #ccc',
              fontSize: '1rem',
            }}
          />
          <button
            onClick={sendMessage}
            style={{
              padding: '12px 20px',
              backgroundColor: '#28a745',
              color: 'white',
              border: 'none',
              borderRadius: '6px',
              cursor: 'pointer',
            }}
          >
            Send
          </button>
        </div>
      </div>
    </div>
  );
};

export default ChatPage;
