import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';

/**
 * Reads the category from either:
 *   1)  /category?category=Design%20%26%20Creative   (query string)
 *   2)  navigate(url, { state:{ category:'Design & Creative' } })
 */
function useCategory() {
  const { search, state } = useLocation();
  const qsCategory = new URLSearchParams(search).get('category');
  return state?.category || qsCategory || '';
}

export default function CategoryProjectList() {
  const category = useCategory();          // ← value coming from Hero
  const [projects, setProjects] = useState([]);
  const [loading,  setLoading]  = useState(true);

  /* Example fetch – replace URL with your real API */
  useEffect(() => {
    if (!category) return;

    setLoading(true);
    fetch(`/api/projects?category=${encodeURIComponent(category)}`)
      .then((res) => res.json())
      .then((data) => setProjects(data))
      .catch(()   => setProjects([]))
      .finally(() => setLoading(false));
  }, [category]);

  if (!category) {
    return <p style={{ padding: '2rem' }}>No category selected.</p>;
  }

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Projects in “{category}”</h2>

      {loading && <p>Loading…</p>}

      {!loading && projects.length === 0 && (
        <p>No projects found for this category.</p>
      )}

      {!loading && projects.length > 0 && (
        <ul>
          {projects.map((p) => (
            <li key={p.id}>{p.title}</li>
          ))}
        </ul>
      )}
    </div>
  );
}
