import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { User, Mail, Eye, EyeOff, Edit, Save, X, UserCheck } from 'lucide-react';

const ProfilePage = () => {
  const navigate = useNavigate();
  const [isEditing, setIsEditing] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  
  // User data state
  const [userData, setUserData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    userType: 'FREELANCER'
  });

  // Temporary state for editing
  const [editData, setEditData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    userType: 'FREELANCER'
  });

  const [errors, setErrors] = useState({});

  useEffect(() => {
    // Check if user is authenticated
    const authStatus = localStorage.getItem('isAuthenticated');
    if (authStatus !== 'true') {
      navigate('/login');
      return;
    }

    // Load actual user data from localStorage (saved during signup)
    const firstName = localStorage.getItem('firstName') || '';
    const lastName = localStorage.getItem('lastName') || '';
    const email = localStorage.getItem('userEmail') || '';
    const userType = localStorage.getItem('userType') || 'FREELANCER';

    // If no user data found, redirect to signup
    if (!firstName || !lastName || !email) {
      alert('No user data found. Please sign up first.');
      navigate('/signup');
      return;
    }

    const loadedUserData = {
      firstName: firstName,
      lastName: lastName,
      email: email,
      password: '••••••••', // Don't show real password
      userType: userType
    };

    setUserData(loadedUserData);
    setEditData(loadedUserData);
  }, [navigate]);

  const validateForm = () => {
    const newErrors = {};
    
    if (!editData.firstName.trim()) {
      newErrors.firstName = 'First name is required';
    }
    
    if (!editData.lastName.trim()) {
      newErrors.lastName = 'Last name is required';
    }
    
    const emailPattern = /^[a-zA-Z0-9]+@gmail\.com$/;
    if (!editData.email) {
      newErrors.email = 'Email is required';
    } else if (!emailPattern.test(editData.email)) {
      newErrors.email = 'Use format like abc123@gmail.com';
    }
    
    if (editData.password !== '••••••••' && editData.password.length > 0 && editData.password.length < 8) {
      newErrors.password = 'Password must be at least 8 characters';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleEdit = () => {
    setIsEditing(true);
    setEditData({...userData});
  };

  const handleCancel = () => {
    setIsEditing(false);
    setEditData({...userData});
    setErrors({});
  };

  const handleSave = async () => {
    if (!validateForm()) return;

    setIsLoading(true);
    
    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      // Update localStorage with new data
      localStorage.setItem('firstName', editData.firstName);
      localStorage.setItem('lastName', editData.lastName);
      localStorage.setItem('userEmail', editData.email);
      localStorage.setItem('userType', editData.userType);
      
      // Only update password in display if a new one was entered
      const updatedUserData = {...editData};
      if (editData.password === '••••••••' || editData.password === '') {
        updatedUserData.password = '••••••••';
      }
      
      setUserData(updatedUserData);
      setIsEditing(false);
      setErrors({});
      
      // Show success message
      alert('Profile updated successfully!');
      
    } catch (error) {
      alert('Failed to update profile. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleInputChange = (field, value) => {
    setEditData(prev => ({
      ...prev,
      [field]: value
    }));
    
    // Clear error for this field
    if (errors[field]) {
      setErrors(prev => ({
        ...prev,
        [field]: ''
      }));
    }
  };

  const userTypeOptions = [
    { value: 'FREELANCER', label: 'Freelancer' },
    { value: 'CLIENT', label: 'Client' }
  ];

  // Helper function to get user initials
  const getUserInitials = () => {
    const firstInitial = userData.firstName.charAt(0).toUpperCase();
    const lastInitial = userData.lastName.charAt(0).toUpperCase();
    return firstInitial + lastInitial;
  };

  // Helper function to format user type display
  const formatUserType = (type) => {
    return type.charAt(0).toUpperCase() + type.slice(1).toLowerCase();
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      padding: '20px'
    }}>
      <div style={{
        maxWidth: '800px',
        margin: '0 auto',
        background: 'white',
        borderRadius: '16px',
        boxShadow: '0 20px 40px rgba(0,0,0,0.1)',
        overflow: 'hidden'
      }}>
        {/* Header */}
        <div style={{
          background: 'linear-gradient(135deg, #00c6ff 0%, #0072ff 100%)',
          color: 'white',
          padding: '30px',
          textAlign: 'center'
        }}>
          <div style={{
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            gap: '15px',
            marginBottom: '10px'
          }}>
            <User size={32} />
            <h1 style={{ margin: 0, fontSize: '2.5rem', fontWeight: 'bold' }}>
              My Profile
            </h1>
          </div>
          <p style={{ margin: 0, opacity: 0.9, fontSize: '1.1rem' }}>
            Manage your personal information and account settings
          </p>
        </div>

        {/* Profile Content */}
        <div style={{ padding: '40px' }}>
          {/* Profile Picture Section */}
          <div style={{
            textAlign: 'center',
            marginBottom: '40px'
          }}>
            <div style={{
              width: '120px',
              height: '120px',
              borderRadius: '50%',
              background: 'linear-gradient(135deg, #00c6ff 0%, #0072ff 100%)',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              margin: '0 auto 20px',
              color: 'white',
              fontSize: '3rem',
              fontWeight: 'bold'
            }}>
              {getUserInitials()}
            </div>
            <h2 style={{
              margin: '0 0 5px 0',
              color: '#1f2937',
              fontSize: '1.8rem'
            }}>
              {userData.firstName} {userData.lastName}
            </h2>
            <p style={{
              margin: 0,
              color: '#6b7280',
              fontSize: '1rem'
            }}>
              {formatUserType(userData.userType)}
            </p>
          </div>

          {/* Profile Form */}
          <div style={{
            background: '#f8fafc',
            borderRadius: '12px',
            padding: '30px'
          }}>
            <div style={{
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              marginBottom: '30px'
            }}>
              <h3 style={{
                margin: 0,
                color: '#1f2937',
                fontSize: '1.3rem'
              }}>
                Personal Information
              </h3>
              
              {(!isEditing) ? (
                <button
                  onClick={handleEdit}
                  style={{
                    display: 'flex',
                    alignItems: 'center',
                    gap: '8px',
                    background: '#00c6ff',
                    color: 'white',
                    border: 'none',
                    borderRadius: '8px',
                    padding: '10px 20px',
                    cursor: 'pointer',
                    fontWeight: '500',
                    transition: 'background 0.3s ease'
                  }}
                  onMouseOver={(e) => e.target.style.background = '#0095c5'}
                  onMouseOut={(e) => e.target.style.background = '#00c6ff'}
                >
                  <Edit size={16} />
                  Edit Profile
                </button>
              ) : (
                <div style={{ display: 'flex', gap: '10px' }}>
                  <button
                    onClick={handleCancel}
                    style={{
                      display: 'flex',
                      alignItems: 'center',
                      gap: '8px',
                      background: '#6b7280',
                      color: 'white',
                      border: 'none',
                      borderRadius: '8px',
                      padding: '10px 15px',
                      cursor: 'pointer',
                      fontWeight: '500'
                    }}
                  >
                    <X size={16} />
                    Cancel
                  </button>
                  <button
                    onClick={handleSave}
                    disabled={isLoading}
                    style={{
                      display: 'flex',
                      alignItems: 'center',
                      gap: '8px',
                      background: isLoading ? '#9ca3af' : '#10b981',
                      color: 'white',
                      border: 'none',
                      borderRadius: '8px',
                      padding: '10px 15px',
                      cursor: isLoading ? 'not-allowed' : 'pointer',
                      fontWeight: '500'
                    }}
                  >
                    <Save size={16} />
                    {isLoading ? 'Saving...' : 'Save'}
                  </button>
                </div>
              )}
            </div>

            {/* Form Fields */}
            <div style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
              gap: '25px'
            }}>
              {/* First Name */}
              <div>
                <label style={{
                  display: 'block',
                  marginBottom: '8px',
                  fontWeight: '600',
                  color: '#374151'
                }}>
                  First Name
                </label>
                {isEditing ? (
                  <input
                    type="text"
                    value={editData.firstName}
                    onChange={(e) => handleInputChange('firstName', e.target.value)}
                    style={{
                      width: '100%',
                      padding: '12px 16px',
                      border: errors.firstName ? '2px solid #ef4444' : '2px solid #e5e7eb',
                      borderRadius: '8px',
                      fontSize: '1rem',
                      transition: 'border-color 0.3s ease',
                      boxSizing: 'border-box'
                    }}
                    onFocus={(e) => e.target.style.borderColor = '#00c6ff'}
                    onBlur={(e) => e.target.style.borderColor = errors.firstName ? '#ef4444' : '#e5e7eb'}
                  />
                ) : (
                  <div style={{
                    padding: '12px 16px',
                    background: 'white',
                    border: '2px solid #e5e7eb',
                    borderRadius: '8px',
                    fontSize: '1rem',
                    color: '#374151'
                  }}>
                    {userData.firstName}
                  </div>
                )}
                {errors.firstName && (
                  <p style={{ color: '#ef4444', fontSize: '0.9rem', margin: '5px 0 0 0' }}>
                    {errors.firstName}
                  </p>
                )}
              </div>

              {/* Last Name */}
              <div>
                <label style={{
                  display: 'block',
                  marginBottom: '8px',
                  fontWeight: '600',
                  color: '#374151'
                }}>
                  Last Name
                </label>
                {isEditing ? (
                  <input
                    type="text"
                    value={editData.lastName}
                    onChange={(e) => handleInputChange('lastName', e.target.value)}
                    style={{
                      width: '100%',
                      padding: '12px 16px',
                      border: errors.lastName ? '2px solid #ef4444' : '2px solid #e5e7eb',
                      borderRadius: '8px',
                      fontSize: '1rem',
                      transition: 'border-color 0.3s ease',
                      boxSizing: 'border-box'
                    }}
                    onFocus={(e) => e.target.style.borderColor = '#00c6ff'}
                    onBlur={(e) => e.target.style.borderColor = errors.lastName ? '#ef4444' : '#e5e7eb'}
                  />
                ) : (
                  <div style={{
                    padding: '12px 16px',
                    background: 'white',
                    border: '2px solid #e5e7eb',
                    borderRadius: '8px',
                    fontSize: '1rem',
                    color: '#374151'
                  }}>
                    {userData.lastName}
                  </div>
                )}
                {errors.lastName && (
                  <p style={{ color: '#ef4444', fontSize: '0.9rem', margin: '5px 0 0 0' }}>
                    {errors.lastName}
                  </p>
                )}
              </div>

              {/* Email */}
              <div>
                <label style={{
                  display: 'block',
                  marginBottom: '8px',
                  fontWeight: '600',
                  color: '#374151'
                }}>
                  <Mail size={16} style={{ marginRight: '5px', verticalAlign: 'middle' }} />
                  Email Address
                </label>
                {isEditing ? (
                  <input
                    type="email"
                    value={editData.email}
                    onChange={(e) => handleInputChange('email', e.target.value)}
                    style={{
                      width: '100%',
                      padding: '12px 16px',
                      border: errors.email ? '2px solid #ef4444' : '2px solid #e5e7eb',
                      borderRadius: '8px',
                      fontSize: '1rem',
                      transition: 'border-color 0.3s ease',
                      boxSizing: 'border-box'
                    }}
                    onFocus={(e) => e.target.style.borderColor = '#00c6ff'}
                    onBlur={(e) => e.target.style.borderColor = errors.email ? '#ef4444' : '#e5e7eb'}
                  />
                ) : (
                  <div style={{
                    padding: '12px 16px',
                    background: 'white',
                    border: '2px solid #e5e7eb',
                    borderRadius: '8px',
                    fontSize: '1rem',
                    color: '#374151'
                  }}>
                    {userData.email}
                  </div>
                )}
                {errors.email && (
                  <p style={{ color: '#ef4444', fontSize: '0.9rem', margin: '5px 0 0 0' }}>
                    {errors.email}
                  </p>
                )}
              </div>

              {/* User Type */}
              <div>
                <label style={{
                  display: 'block',
                  marginBottom: '8px',
                  fontWeight: '600',
                  color: '#374151'
                }}>
                  <UserCheck size={16} style={{ marginRight: '5px', verticalAlign: 'middle' }} />
                  User Type
                </label>
                {isEditing ? (
                  <select
                    value={editData.userType}
                    onChange={(e) => handleInputChange('userType', e.target.value)}
                    style={{
                      width: '100%',
                      padding: '12px 16px',
                      border: '2px solid #e5e7eb',
                      borderRadius: '8px',
                      fontSize: '1rem',
                      transition: 'border-color 0.3s ease',
                      boxSizing: 'border-box',
                      background: 'white'
                    }}
                    onFocus={(e) => e.target.style.borderColor = '#00c6ff'}
                    onBlur={(e) => e.target.style.borderColor = '#e5e7eb'}
                  >
                    {userTypeOptions.map(option => (
                      <option key={option.value} value={option.value}>
                        {option.label}
                      </option>
                    ))}
                  </select>
                ) : (
                  <div style={{
                    padding: '12px 16px',
                    background: 'white',
                    border: '2px solid #e5e7eb',
                    borderRadius: '8px',
                    fontSize: '1rem',
                    color: '#374151'
                  }}>
                    {formatUserType(userData.userType)}
                  </div>
                )}
              </div>

              {/* Password */}
              <div>
                <label style={{
                  display: 'block',
                  marginBottom: '8px',
                  fontWeight: '600',
                  color: '#374151'
                }}>
                  Password
                </label>
                {isEditing ? (
                  <div style={{ position: 'relative' }}>
                    <input
                      type={showPassword ? 'text' : 'password'}
                      value={editData.password}
                      onChange={(e) => handleInputChange('password', e.target.value)}
                      placeholder="Enter new password (leave blank to keep current)"
                      style={{
                        width: '100%',
                        padding: '12px 45px 12px 16px',
                        border: errors.password ? '2px solid #ef4444' : '2px solid #e5e7eb',
                        borderRadius: '8px',
                        fontSize: '1rem',
                        transition: 'border-color 0.3s ease',
                        boxSizing: 'border-box'
                      }}
                      onFocus={(e) => e.target.style.borderColor = '#00c6ff'}
                      onBlur={(e) => e.target.style.borderColor = errors.password ? '#ef4444' : '#e5e7eb'}
                    />
                    <button
                      type="button"
                      onClick={() => setShowPassword(!showPassword)}
                      style={{
                        position: 'absolute',
                        right: '12px',
                        top: '50%',
                        transform: 'translateY(-50%)',
                        background: 'none',
                        border: 'none',
                        cursor: 'pointer',
                        color: '#6b7280'
                      }}
                    >
                      {showPassword ? <EyeOff size={18} /> : <Eye size={18} />}
                    </button>
                  </div>
                ) : (
                  <div style={{
                    padding: '12px 16px',
                    background: 'white',
                    border: '2px solid #e5e7eb',
                    borderRadius: '8px',
                    fontSize: '1rem',
                    color: '#374151'
                  }}>
                    ••••••••
                  </div>
                )}
                {errors.password && (
                  <p style={{ color: '#ef4444', fontSize: '0.9rem', margin: '5px 0 0 0' }}>
                    {errors.password}
                  </p>
                )}
              </div>
            </div>
          </div>

          {/* Back Button */}
          <div style={{ textAlign: 'center', marginTop: '30px' }}>
            <button
              onClick={() => navigate(-1)}
              style={{
                background: 'linear-gradient(135deg, #6b7280 0%, #374151 100%)',
                color: 'white',
                border: 'none',
                borderRadius: '8px',
                padding: '12px 30px',
                fontSize: '1rem',
                fontWeight: '500',
                cursor: 'pointer',
                transition: 'transform 0.2s ease'
              }}
              onMouseOver={(e) => e.target.style.transform = 'translateY(-2px)'}
              onMouseOut={(e) => e.target.style.transform = 'translateY(0)'}
            >
              Go Back
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProfilePage;
