import React, { useState, useEffect } from 'react';
import { useTranslation } from 'react-i18next';
import { useNavigate } from 'react-router-dom';
import { User, LogOut, ChevronDown, FolderPlus } from 'lucide-react';
import './Navbar.css';

const Navbar = () => {
  const { t, i18n } = useTranslation();
  const navigate = useNavigate();
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userEmail, setUserEmail] = useState('');
  const [showUserMenu, setShowUserMenu] = useState(false);

  useEffect(() => {
    const authStatus = localStorage.getItem('isAuthenticated');
    const email = localStorage.getItem('userEmail');

    if (authStatus === 'true') {
      setIsAuthenticated(true);
      setUserEmail(email || '');
    }
  }, []);

  const changeLanguage = (lng) => {
    i18n.changeLanguage(lng);
  };

  const handleLogout = () => {
    localStorage.removeItem('isAuthenticated');
    localStorage.removeItem('userEmail');
    setIsAuthenticated(false);
    setUserEmail('');
    setShowUserMenu(false);
    navigate('/');
  };

  const handleProfileClick = () => {
    navigate('/profile');
    setShowUserMenu(false);
  };

  const handleCreateProjectClick = () => {
    navigate('/clientpro');
    setShowUserMenu(false);
  };

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <h2 className="logo" onClick={() => navigate('/')} style={{ cursor: 'pointer' }}>
          SkillsFusion<span className="dot">.</span>
        </h2>
      </div>

      <div className="navbar-right-group">
        <ul className="navbar-center">
          <li>
            <a onClick={() => navigate('/premium')} className="nav-link" style={{ cursor: 'pointer' }}>
              {t('fusionPro')}
            </a>
          </li>

          <li>
            <button
              onClick={() => navigate('/explore')}
              className="nav-link"
              style={{
                background: 'none',
                border: 'none',
                cursor: 'pointer',
                color: 'inherit',
                font: 'inherit',
                padding: 0,
              }}
            >
              {t('explore')}
            </button>
          </li>

          <li className="lang-dropdown">
            üåê {i18n.language.toUpperCase()}
            <ul className="lang-menu">
              <li onClick={() => changeLanguage('en')}>English</li>
              <li onClick={() => changeLanguage('fr')}>Fran√ßais</li>
              <li onClick={() => changeLanguage('de')}>Deutsch</li>
              <li onClick={() => changeLanguage('es')}>Espa√±ol</li>
              <li onClick={() => changeLanguage('pt')}>Portugu√™s</li>
            </ul>
          </li>
        </ul>

        <div className="navbar-right">
          {isAuthenticated ? (
            <div className="user-menu-container">
              <div
                className="user-menu-trigger"
                onClick={() => setShowUserMenu(!showUserMenu)}
                style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '8px',
                  padding: '8px 12px',
                  background: '#ffffff',
                  borderRadius: '8px',
                  cursor: 'pointer',
                  border: '1px solid #ccc',
                  color: '#28a740', // ‚úÖ Light green text
                  fontWeight: '600',
                  boxShadow: '0 0 8px hsla(134, 61.40%, 40.60%, 0.10)' // ‚úÖ Green glow
                }}
              >
                <User size={18} />
                <span>{userEmail.split('@')[0]}</span>
                <ChevronDown size={16} />
              </div>

              {showUserMenu && (
                <div
                  className="user-dropdown-menu"
                  style={{
                    position: 'absolute',
                    top: '100%',
                    right: 0,
                    background: 'white',
                    border: '1px solid #e0e0e0',
                    borderRadius: '8px',
                    boxShadow: '0 4px 12px rgba(0,0,0,0.15)',
                    minWidth: '180px',
                    zIndex: 1000,
                    marginTop: '8px',
                  }}
                >
                  <div
                    onClick={handleProfileClick}
                    style={{
                      display: 'flex',
                      alignItems: 'center',
                      gap: '10px',
                      padding: '12px 16px',
                      cursor: 'pointer',
                      borderBottom: '1px solid #f0f0f0',
                      transition: 'background 0.2s',
                    }}
                    onMouseOver={(e) => e.currentTarget.style.background = '#d1e7dd'}
                    onMouseOut={(e) => e.currentTarget.style.background = 'white'}
                  >
                    <User size={16} />
                    <span>Profile</span>
                  </div>

                  <div
                    onClick={handleCreateProjectClick}
                    style={{
                      display: 'flex',
                      alignItems: 'center',
                      gap: '10px',
                      padding: '12px 16px',
                      cursor: 'pointer',
                      borderBottom: '1px solid #f0f0f0',
                      transition: 'background 0.2s',
                    }}
                    onMouseOver={(e) => e.currentTarget.style.background = '#d1e7dd'}
                    onMouseOut={(e) => e.currentTarget.style.background = 'white'}
                  >
                    <FolderPlus size={16} />
                    <span>Create Project</span>
                  </div>

                  <div
                    onClick={handleLogout}
                    style={{
                      display: 'flex',
                      alignItems: 'center',
                      gap: '10px',
                      padding: '12px 16px',
                      cursor: 'pointer',
                      color: '#dc3545',
                      transition: 'background 0.2s',
                    }}
                    onMouseOver={(e) => e.currentTarget.style.background = '#fff5f5'}
                    onMouseOut={(e) => e.currentTarget.style.background = 'white'}
                  >
                    <LogOut size={16} />
                    <span>Logout</span>
                  </div>
                </div>
              )}
            </div>
          ) : (
            <>
              <a onClick={() => navigate('/login')} className="login-btn" style={{ cursor: 'pointer' }}>
                {t('login')}
              </a>
              <a onClick={() => navigate('/signup')} className="join-btn" style={{ cursor: 'pointer' }}>
                {t('signup')}
              </a>
            </>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
