import React from 'react';
import {
  Code, Brush, Smartphone, FileText, PlayCircle,
  Image, Music, Users, Briefcase
} from 'lucide-react';

const categories = [
  { icon: <Code size={28} />, label: "Programming & Tech" },
  { icon: <Brush size={28} />, label: "Graphics & Design" },
  { icon: <Smartphone size={28} />, label: "Digital Marketing" },
  { icon: <FileText size={28} />, label: "Writing & Translation" },
  { icon: <PlayCircle size={28} />, label: "Video & Animation" },
  { icon: <Image size={28} />, label: "AI Services" },
  { icon: <Music size={28} />, label: "Music & Audio" },
  { icon: <Users size={28} />, label: "Business" },
  { icon: <Briefcase size={28} />, label: "Consulting" }
];

export default function CategoriesSection() {
  return (
    <div className="py-10 px-4 md:px-16 bg-white">
      <h2 className="text-2xl font-semibold mb-6 text-center">Explore Categories</h2>
      <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-6 justify-items-center">
        {categories.map((cat, idx) => (
          <div
            key={idx}
            className="flex flex-col items-center justify-center p-4 bg-white rounded-xl shadow hover:shadow-lg transition-all border border-gray-100 w-40 h-32 text-center"
          >
            <div className="mb-2 text-gray-700">{cat.icon}</div>
            <div className="text-sm font-medium text-gray-800">{cat.label}</div>
          </div>
        ))}
      </div>
    </div>
  );
}
