import React, { useState } from 'react';
import { useTranslation } from 'react-i18next';
import './Navbar.css';

const Navbar = () => {
  const { t, i18n } = useTranslation();
  const [showLang, setShowLang] = useState(false);

  const changeLanguage = (lng) => {
    i18n.changeLanguage(lng);
    setShowLang(false);
  };

  return (
    <nav className="navbar">
      {/* Fiverr Logo */}
      <div className="navbar-logo">
        <h2>fiverr<span className="dot">.</span></h2>
      </div>

      {/* Right Side Navigation */}
      <div className="navbar-right">
        <ul className="nav-links">
          <li><a href="#">Fiverr Pro ‚ñæ</a></li>
          <li><a href="#">Explore ‚ñæ</a></li>

          {/* Language Dropdown */}
          <li
            className="lang-dropdown"
            onMouseEnter={() => setShowLang(true)}
            onMouseLeave={() => setShowLang(false)}
          >
            üåê {i18n.language.toUpperCase()}
            {showLang && (
              <ul className="lang-menu">
                <li onClick={() => changeLanguage('en')}>English</li>
                <li onClick={() => changeLanguage('fr')}>Fran√ßais</li>
                <li onClick={() => changeLanguage('de')}>Deutsch</li>
                <li onClick={() => changeLanguage('es')}>Espa√±ol</li>
                <li onClick={() => changeLanguage('pt')}>Portugu√™s</li>
                <li onClick={() => changeLanguage('it')}>Italiano</li>
              </ul>
            )}
          </li>

          {/* Auth buttons */}
          <li><a href="#" className="login-btn">{t('login')}</a></li>
          <li><a href="#" className="signup-btn">{t('signup')}</a></li>
        </ul>
      </div>
    </nav>
  );
};

export default Navbar;
