import React, { useState, useEffect, useRef } from 'react';
import '@fortawesome/fontawesome-free/css/all.min.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';
import { useTranslation } from 'react-i18next';
import { useNavigate } from 'react-router-dom';
import './Hero.css';

const Hero = () => {
  const { t } = useTranslation();
  const navigate = useNavigate();

  /* ‚îÄ‚îÄ‚îÄ Auth / role state ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ */
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userType, setUserType] = useState('');

  const updateAuthState = () => {
    const auth = localStorage.getItem('isAuthenticated') === 'true';
    const role = (localStorage.getItem('userType') || '').toUpperCase();
    setIsAuthenticated(auth);
    setUserType(role);
  };

  useEffect(() => {
    updateAuthState();                                 // initial
    const int = setInterval(updateAuthState, 1000);    // poll localStorage

    const handleStorage = () => updateAuthState();     // cross‚Äëtab updates
    window.addEventListener('storage', handleStorage);
    window.addEventListener('authStateChanged', handleStorage);

    return () => {
      clearInterval(int);
      window.removeEventListener('storage', handleStorage);
      window.removeEventListener('authStateChanged', handleStorage);
    };
  }, []);

  /* ‚îÄ‚îÄ‚îÄ Search / dropdown state ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ */
  const [searchTerm, setSearchTerm] = useState('');
  const [allCategories, setAllCategories] = useState([]);
  const [filteredCategories, setFilteredCategories] = useState([]);
  const [showSuggestions, setShowSuggestions] = useState(false);

  const searchRef      = useRef(null);
  const suggestionsRef = useRef(null);

  /* Fetch category list once */
  useEffect(() => {
    fetch('http://localhost:8080/api/projects/categories')
      .then((r) => r.json())
      .then((data) => Array.isArray(data) && setAllCategories(data))
      .catch((err) => console.error('Failed to load categories:', err));
  }, []);

  /* Close dropdown when clicking outside */
  useEffect(() => {
    const handleClick = (e) => {
      if (
        searchRef.current      && !searchRef.current.contains(e.target) &&
        suggestionsRef.current && !suggestionsRef.current.contains(e.target)
      ) {
        setShowSuggestions(false);
      }
    };
    document.addEventListener('mousedown', handleClick);
    return () => document.removeEventListener('mousedown', handleClick);
  }, []);

  /* Helpers */
  const navigateToCategory = (cat) => {
    navigate(`/category?category=${encodeURIComponent(cat)}`, { state: { category: cat } });
    setShowSuggestions(false);
  };

  const handleChange = (e) => {
    const val = e.target.value;
    setSearchTerm(val);

    if (!val.trim()) {
      setFilteredCategories([]);
      setShowSuggestions(false);
      return;
    }

    const lower = val.toLowerCase();
    const starts = allCategories.filter((c) => c.toLowerCase().startsWith(lower));
    const others = allCategories.filter(
      (c) => !c.toLowerCase().startsWith(lower) && c.toLowerCase().includes(lower)
    );
    const sorted = [...starts, ...others];
    setFilteredCategories(sorted);
    setShowSuggestions(sorted.length > 0);
  };

  const handleSearch = () => {
    const trimmed = searchTerm.trim();
    if (trimmed) navigateToCategory(trimmed);
  };

  const showSearchUI = isAuthenticated && userType === 'FREELANCER';

  /* ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ */
  return (
    <section className="hero-section">
      {/* üéû Carousel */}
      <div id="videoCarousel" className="carousel slide carousel-fade" data-bs-ride="carousel">
        <div className="carousel-inner">
          {['hero.mp4', 'hero2.mp4', 'hero3.mp4'].map((v, i) => (
            <div key={v} className={`carousel-item ${i === 0 ? 'active' : ''}`}>
              <video className="d-block w-100" autoPlay muted loop>
                <source src={`/Assets/${v}`} type="video/mp4" />
              </video>
            </div>
          ))}
        </div>
        <button className="carousel-control-prev" data-bs-target="#videoCarousel" data-bs-slide="prev">
          <span className="carousel-control-prev-icon"></span>
        </button>
        <button className="carousel-control-next" data-bs-target="#videoCarousel" data-bs-slide="next">
          <span className="carousel-control-next-icon"></span>
        </button>
      </div>

      {/* üìù Caption */}
      <div className="hero-content">
        {!showSearchUI ? (
          <>
            <h1 style={{ fontSize: '3rem', fontWeight: 'bold', color: '#fff', marginTop: '20px' }}>
              Welcome to SkillNest
            </h1>
            <p style={{ fontSize: '1.2rem', color: '#fff', marginTop: '10px' }}>
              Your one-stop destination to connect with expert freelancers & clients.
            </p>
          </>
        ) : (
          <>
            <h1 className="display-7 fw-bold">{t('heroTitle')}</h1>
            <p>{t('heroSubtitle')}</p>
          </>
        )}

        {/* üîç Search (freelancer only) */}
        {showSearchUI && (
          <div className="hero-search-container">
            <div className="hero-search position-relative" ref={searchRef}>
              <div className="hero-search-bar">
                <input
                  type="text"
                  placeholder={t('searchPlaceholder', 'Search for any service...')}
                  value={searchTerm}
                  onChange={handleChange}
                  onFocus={() => filteredCategories.length && setShowSuggestions(true)}
                />
                <button onClick={handleSearch}>
                  <i className="fas fa-search"></i>
                </button>
              </div>

              {showSuggestions && (
                <ul
                  ref={suggestionsRef}
                  className="category-dropdown position-absolute bg-white border rounded shadow"
                  style={{
                    listStyle: 'none',
                    padding: 0,
                    marginTop: '5px',
                    top: '100%',
                    left: '50%',
                    transform: 'translateX(-50%)',
                    maxHeight: '220px',
                    overflowY: 'auto',
                    zIndex: 1000,
                    width: '100%',
                  }}
                >
                  {filteredCategories.map((cat) => (
                    <li
                      key={cat}
                      style={{ padding: '10px', cursor: 'pointer', borderBottom: '1px solid #eee' }}
                      onClick={() => navigateToCategory(cat)}
                      onMouseEnter={(e) => (e.currentTarget.style.background = '#f8f9fa')}
                      onMouseLeave={(e) => (e.currentTarget.style.background = '#fff')}
                    >
                      <small className="text-muted">Category</small>
                      <div className="fw-medium text-dark">{cat}</div>
                    </li>
                  ))}
                </ul>
              )}

              {/* Quick links */}
              <div className="hero-suggestions mt-3 d-flex gap-2 justify-content-center flex-wrap">
                <button onClick={() => navigateToCategory('Development & IT')}>Development & IT ‚Üí</button>
                <button onClick={() => navigateToCategory('Design & Creative')}>Design & Creative ‚Üí</button>
                <button onClick={() => navigateToCategory('AI Services')}>AI Services ‚Üí</button>
              </div>
            </div>
          </div>
        )}
      </div>
    </section>
  );
};

export default Hero;
