import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const FreelancerChatList = () => {
  const [clients, setClients] = useState([]);
  const freelancerEmail = localStorage.getItem('userEmail');
  const navigate = useNavigate();

  useEffect(() => {
    if (!freelancerEmail) return;

    fetch(`http://localhost:8081/api/applications/freelancer/${freelancerEmail}`)
      .then((res) => {
        if (!res.ok) throw new Error('Failed to fetch clients');
        return res.json();
      })
      .then((data) => setClients(data))
      .catch((err) => console.error('Error fetching clients:', err));
  }, [freelancerEmail]);

  const handleChat = (clientEmail) => {
    navigate(`/chat?to=${clientEmail}`);
  };

  const handleBack = () => {
    navigate('/');
  };

  return (
    <div style={styles.container}>
      <div style={styles.topBar}>
        <button onClick={handleBack} style={styles.backButton}>‚Üê Back</button>
      </div>
      <h2 style={styles.heading}>Your Clients</h2>
      {clients.length === 0 ? (
        <p style={styles.noClients}>You haven't applied to any projects yet.</p>
      ) : (
        <ul style={styles.list}>
          {clients.map((c, idx) => (
            <li key={idx} style={styles.card}>
              <p><strong>Client Name:</strong> {c.project.clientName}</p>
              <p><strong>Email:</strong> {c.project.clientEmail}</p>
              <p><strong>Project:</strong> {c.project.title}</p>
              <button
                onClick={() => handleChat(c.project.clientEmail)}
                style={styles.chatButton}
              >
                Chat
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

const styles = {
  container: {
    padding: '2rem',
    maxWidth: '900px',
    margin: '0 auto',
    fontFamily: "'Segoe UI', sans-serif"
  },
  topBar: {
    display: 'flex',
    justifyContent: 'flex-start',
    marginBottom: '1rem'
  },
  backButton: {
    backgroundColor: '#b3f0b3',
    color: '#155724',
    border: 'none',
    borderRadius: '6px',
    padding: '0.5rem 1rem',
    fontWeight: 'bold',
    fontSize: '14px',
    cursor: 'pointer',
    boxShadow: '0 2px 5px rgba(0, 0, 0, 0.1)'
  },
  heading: {
    textAlign: 'center',
    color: '#007acc',
    marginBottom: '1.5rem'
  },
  noClients: {
    textAlign: 'center',
    color: '#666'
  },
  list: {
    listStyle: 'none',
    padding: 0,
    display: 'grid',
    gap: '1.5rem'
  },
  card: {
    padding: '1.5rem',
    borderRadius: '10px',
    backgroundColor: '#f9fcff',
    border: '1px solid #cce6ff',
    boxShadow: '0 4px 12px rgba(0, 123, 255, 0.1)'
  },
  chatButton: {
    marginTop: '1rem',
    padding: '0.6rem 1.2rem',
    backgroundColor: '#00aaff',
    color: 'white',
    border: 'none',
    borderRadius: '8px',
    fontWeight: 'bold',
    cursor: 'pointer',
    transition: 'background 0.3s ease'
  }
};

export default FreelancerChatList;
