import i18n from 'i18next';
import { initReactI18next } from 'react-i18next';
import LanguageDetector from 'i18next-browser-languagedetector';

i18n
  .use(LanguageDetector)
  .use(initReactI18next)
  .init({
    resources: {
      en: {
        translation: {
          welcome: 'Welcome',
          login: 'Login',
          signup: 'Signup',
          heroTitle: 'Find the perfect freelancer',
          heroSubtitle: 'Get your work done by top-rated professionals on SkillsFusion.'
        }
      },
      fr: {
        translation: {
          welcome: 'Bienvenue',
          login: 'Connexion',
          signup: "S'inscrire",
          heroTitle: 'Trouvez le freelance parfait',
          heroSubtitle: 'Faites réaliser votre travail par des professionnels bien notés sur SkillsFusion.'
        }
      },
      de: {
        translation: {
          welcome: 'Willkommen',
          login: 'Anmelden',
          signup: 'Registrieren',
          heroTitle: 'Finde den perfekten Freelancer',
          heroSubtitle: 'Lassen Sie Ihre Arbeit von Top-Profis auf SkillsFusion erledigen.'
        }
      },
      es: {
        translation: {
          welcome: 'Bienvenido',
          login: 'Iniciar sesión',
          signup: 'Regístrate',
          heroTitle: 'Encuentra al freelancer perfecto',
          heroSubtitle: 'Haz que tu trabajo sea realizado por profesionales calificados en SkillsFusion.'
        }
      },
      pt: {
        translation: {
          welcome: 'Bem-vindo',
          login: 'Entrar',
          signup: 'Cadastrar-se',
          heroTitle: 'Encontre o freelancer perfeito',
          heroSubtitle: 'Tenha seu trabalho feito por profissionais avaliados no SkillsFusion.'
        }
      },
      it: {
        translation: {
          welcome: 'Benvenuto',
          login: 'Accedi',
          signup: 'Iscriviti',
          heroTitle: 'Trova il freelancer perfetto',
          heroSubtitle: 'Fai svolgere il tuo lavoro da professionisti top su SkillsFusion.'
        }
      }
    },
    fallbackLng: 'en',
    interpolation: {
      escapeValue: false
    }
  });

export default i18n;
