import React, { useState, useEffect } from 'react';
import './Hero.css';

const AnimatedText = ({ children, delay = 0 }) => {
  const [visible, setVisible] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => setVisible(true), delay);
    return () => clearTimeout(timer);
  }, [delay]);

  return (
    <div className={`animated-text ${visible ? 'visible' : ''}`}>
      {children}
    </div>
  );
};

const Hero = () => {
  const [mousePos, setMousePos] = useState({ x: 0, y: 0 });

  useEffect(() => {
    const handleMouseMove = (e) => {
      setMousePos({
        x: (e.clientX - window.innerWidth / 2) / 200,
        y: (e.clientY - window.innerHeight / 2) / 200
      });
    };

    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  return (
    <div className="hero-container">
      <div className="hero-background" style={{ transform: `translate(${mousePos.x}px, ${mousePos.y}px)` }}>
        <div className="circle purple-blue"></div>
        <div className="circle blue-cyan"></div>
        <div className="circle cyan-teal"></div>
      </div>

      <main className="hero-content">
        <div className="hero-left">
          <AnimatedText delay={500}>
            <span className="tagline">ðŸš€ Welcome to the Future of Work</span>
          </AnimatedText>

          <AnimatedText delay={800}>
            <h1 className="hero-title">
              <span className="line1">Welcome to</span><br />
              <span className="line2">SkillsFusion</span>
            </h1>
          </AnimatedText>

          <AnimatedText delay={1200}>
            <p className="hero-description">
              Connecting talent with opportunity seamlessly. Experience the future of freelancing with cutting-edge technology and limitless possibilities.
            </p>
          </AnimatedText>

          <AnimatedText delay={1600}>
            <div className="hero-buttons">
              <button className="btn primary">Get Started</button>
              <button className="btn secondary">Learn More</button>
            </div>
          </AnimatedText>
        </div>

        <div className="hero-right">
          {/* Future Image or Animation Here */}
        </div>
      </main>
    </div>
  );
};

export default Hero;
