import React, { useEffect, useState, useRef } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

const allCategories = [
  'Development & IT',
  'Design & Creative',
  'AI Services',
  'Sales & Marketing',
  'Engineering & Architecture',
  'Writing & Translation',
  'Customer Support',
  'Data Science',
  'Cybersecurity',
  'Mobile App Development',
];

const CategoryProjectList = () => {
  const location   = useLocation();
  const navigate   = useNavigate();
  const query      = new URLSearchParams(location.search);
  const initialCat = query.get('category') || '';

  const [category, setCategory]                 = useState(initialCat);
  const [projects, setProjects]                 = useState([]);
  const [loading,  setLoading]                  = useState(false);
  const [error,    setError]                    = useState('');
  const [filtered, setFiltered]                 = useState([]);
  const [open,     setOpen]                     = useState(false);

  const inputRef       = useRef(null);
  const suggestionsRef = useRef(null);

  /* Fetch projects for the active category */
  useEffect(() => {
    if (!category) return;

    setLoading(true);
    setError('');
    window.scrollTo(0, 0);

    fetch(`http://localhost:8081/api/projects/category/${encodeURIComponent(category)}`)
      .then((res) => {
        if (!res.ok) throw new Error('Failed to fetch projects');
        return res.json();
      })
      .then((data) => { setProjects(data); setLoading(false); })
      .catch((err) => { console.error(err); setError('Something went wrong while fetching projects.'); setLoading(false); });
  }, [category]);

  /* Close dropdown when clicking outside */
  useEffect(() => {
    const handler = (e) => {
      if (
        suggestionsRef.current && !suggestionsRef.current.contains(e.target) &&
        inputRef.current       && !inputRef.current.contains(e.target)
      ) {
        setOpen(false);
      }
    };
    document.addEventListener('mousedown', handler);
    return () => document.removeEventListener('mousedown', handler);
  }, []);

  /* Handle typing in search box */
  const handleChange = (e) => {
    const val = e.target.value;
    setCategory(val);

    if (!val.trim()) {
      setFiltered([]);
      setOpen(false);
      return;
    }

    const matches = allCategories.filter(c =>
      c.toLowerCase().includes(val.toLowerCase())
    );
    setFiltered(matches);
    setOpen(matches.length > 0);
  };

  /* When user picks a suggestion */
  const chooseCategory = (cat) => {
    setCategory(cat);
    setOpen(false);
    navigate(`/category?category=${encodeURIComponent(cat)}`);
  };

  return (
    <div className="category-page" style={{ padding: '20px' }}>
      <h2 style={{ marginBottom: '20px' }}>
        Projects in Category:&nbsp;
        <span style={{ color: '#007bff' }}>{category || 'N/A'}</span>
      </h2>

      {/* ───── Category Search Input + Dropdown */}
      <div style={{ position: 'relative', maxWidth: '400px', marginBottom: '30px' }}>
        <input
          ref={inputRef}
          type="text"
          placeholder="Search category..."
          value={category}
          onChange={handleChange}
          onFocus={() => { if (filtered.length) setOpen(true); }}
          style={{
            width: '100%',
            padding: '10px',
            borderRadius: '5px',
            border: '1px solid #ccc',
            fontSize: '1rem',
          }}
        />

        {open && (
          <ul
            ref={suggestionsRef}
            style={{
              position: 'absolute',
              top: '42px',
              left: 0,
              right: 0,
              zIndex: 1000,
              listStyle: 'none',
              margin: 0,
              padding: 0,
              background: '#fff',
              border: '1px solid #ccc',
              borderRadius: '5px',
              maxHeight: '180px',
              overflowY: 'auto',
              boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
            }}
          >
            {filtered.map((c) => (
              <li
                key={c}
                onClick={() => chooseCategory(c)}
                style={{
                  padding: '10px',
                  cursor: 'pointer',
                  borderBottom: '1px solid #eee',
                }}
                onMouseEnter={(e) => (e.currentTarget.style.background = '#f1f1f1')}
                onMouseLeave={(e) => (e.currentTarget.style.background = '#fff')}
              >
                {c}
              </li>
            ))}
          </ul>
        )}
      </div>

      {/* ───── Project List (unchanged) */}
      {loading ? (
        <p>Loading projects...</p>
      ) : error ? (
        <p style={{ color: 'red' }}>{error}</p>
      ) : projects.length === 0 ? (
        <p>No projects found in this category.</p>
      ) : (
        <ul className="project-list" style={{ listStyle: 'none', padding: 0 }}>
          {projects.map((p, i) => (
            <li
              key={i}
              className="project-card"
              style={{
                background: '#f9f9f9',
                padding: '15px',
                marginBottom: '15px',
                borderRadius: '8px',
                boxShadow: '0 2px 5px rgba(0,0,0,0.1)',
              }}
            >
              <h3>{p.title}</h3>
              <p><strong>Description:</strong> {p.description}</p>
              <p><strong>Budget:</strong> ₹{p.budget}</p>
              <p><strong>Deadline:</strong> {p.deadline}</p>
              <p><strong>Client Name:</strong> {p.clientName}</p>
              <p><strong>Client Email:</strong> {p.clientEmail}</p>
              <p><strong>Contact Number:</strong> {p.contactNumber}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default CategoryProjectList;
