import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './CategoryProjectList.css';       // ← keeps the new CSS

const CategoryProjectList = () => {
  const location   = useLocation();
  const navigate   = useNavigate();
  const queryParams = new URLSearchParams(location.search);
  const category    = queryParams.get('category');

  const [projects, setProjects] = useState([]);

  /* ─── Fetch projects for the selected category ─────────────────── */
  useEffect(() => {
    if (!category) return;
    fetch(`http://localhost:8081/api/projects/category/${encodeURIComponent(category)}`)
      .then((res) => {
        if (!res.ok) throw new Error('Network response was not ok');
        return res.json();
      })
      .then((data) => setProjects(data))
      .catch((err) => console.error('Error fetching:', err));
  }, [category]);

  /* ─── Handlers ──────────────────────────────────────────────────── */
  const handleApply = (projectId) => navigate(`/apply/${projectId}`);
  const handleBack  = () => navigate('/');

  /* ─── JSX ───────────────────────────────────────────────────────── */
  return (
    <div className="catproj-page">
      <h2 className="catproj-title">
        Projects in Category: <span>{category || 'N/A'}</span>
      </h2>

      {projects.length === 0 ? (
        <p className="catproj-status-msg">No projects found in this category.</p>
      ) : (
        <ul className="catproj-project-list">
          {projects.map((p) => (
            <li key={p.id || p.title} className="catproj-card">
              <h3>{p.title}</h3>
              <p><strong>Description:</strong> {p.description}</p>
              <p><strong>Budget:</strong> ₹{p.budget}</p>
              <p><strong>Deadline:</strong> {p.deadline}</p>
              <p><strong>Client Name:</strong> {p.clientName}</p>
              <p><strong>Client Email:</strong> {p.clientEmail}</p>
              <p><strong>Contact Number:</strong> {p.contactNumber}</p>

              {/* Centered action buttons */}
              <div className="catproj-btn-row">
                <button onClick={() => handleApply(p.id)} className="catproj-apply-btn apply">
                  Apply
                </button>
                <button onClick={handleBack} className="catproj-apply-btn back">
                  ← Home
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default CategoryProjectList;
